import React from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import ReleasesGrid from '../../components/ReleasesGrid';

// Mock releases for Build It Tech
const mockReleases = [
  {
    id: 'tech1',
    title: 'Digital Horizon EP',
    artist: 'TechMaster',
    artwork: 'https://via.placeholder.com/300',
    releaseDate: '2024-02-01',
    genre: 'Techno',
    beatportLink: 'https://www.beatport.com/release/tech-sample/1',
    spotifyLink: 'https://open.spotify.com/album/tech1',
    tracks: [
      {
        id: 'tech_t1',
        title: 'Digital Horizon (Original Mix)',
        artist: 'TechMaster',
        duration: '6:45',
        spotifyId: 'spotify:track:tech1_1',
      },
      {
        id: 'tech_t2',
        title: 'Neural Network (Original Mix)',
        artist: 'TechMaster',
        duration: '7:30',
        spotifyId: 'spotify:track:tech1_2',
      },
    ],
    label: 'tech',
  },
  {
    id: 'tech2',
    title: 'Quantum Drive',
    artist: 'Circuit Breaker',
    artwork: 'https://via.placeholder.com/300',
    releaseDate: '2024-01-20',
    genre: 'Hard Techno',
    beatportLink: 'https://www.beatport.com/release/tech-sample/2',
    spotifyLink: 'https://open.spotify.com/album/tech2',
    tracks: [
      {
        id: 'tech_t3',
        title: 'Quantum Drive (Original Mix)',
        artist: 'Circuit Breaker',
        duration: '6:15',
        spotifyId: 'spotify:track:tech2_1',
      },
      {
        id: 'tech_t4',
        title: 'System Overload (Original Mix)',
        artist: 'Circuit Breaker',
        duration: '7:00',
        spotifyId: 'spotify:track:tech2_2',
      },
    ],
    label: 'tech',
  },
  {
    id: 'tech3',
    title: 'Binary Pulse',
    artist: 'Data Stream',
    artwork: 'https://via.placeholder.com/300',
    releaseDate: '2024-01-10',
    genre: 'Industrial Techno',
    beatportLink: 'https://www.beatport.com/release/tech-sample/3',
    spotifyLink: 'https://open.spotify.com/album/tech3',
    tracks: [
      {
        id: 'tech_t5',
        title: 'Binary Pulse (Original Mix)',
        artist: 'Data Stream',
        duration: '8:00',
        spotifyId: 'spotify:track:tech3_1',
      },
      {
        id: 'tech_t6',
        title: 'Code Sequence (Original Mix)',
        artist: 'Data Stream',
        duration: '7:45',
        spotifyId: 'spotify:track:tech3_2',
      },
    ],
    label: 'tech',
  },
];

const BuildItTechHome = ({ navigation }) => {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>BUILD IT TECH</Text>
        <Text style={styles.subtitle}>Hard-Hitting Techno Label</Text>
      </View>

      <View style={styles.content}>
        <Text style={styles.sectionTitle}>Latest Releases</Text>
        <ReleasesGrid releases={mockReleases} label="tech" />

        <TouchableOpacity 
          style={styles.button}
          onPress={() => navigation.navigate('About')}
        >
          <Text style={styles.buttonText}>About the Label</Text>
        </TouchableOpacity>

        <TouchableOpacity 
          style={styles.button}
          onPress={() => navigation.navigate('Contact')}
        >
          <Text style={styles.buttonText}>Contact Us</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#121212',
  },
  header: {
    padding: 20,
    alignItems: 'center',
    borderBottomWidth: 1,
    borderBottomColor: '#333',
    backgroundColor: 'rgba(26, 26, 26, 0.9)',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#00ff00', // Green color for Tech label
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 16,
    color: '#888888',
  },
  content: {
    padding: 20,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#1a472a', // Darker green for Tech buttons
    padding: 15,
    borderRadius: 8,
    alignItems: 'center',
    marginTop: 20,
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default BuildItTechHome;
