import React, { useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { View, Text, TouchableOpacity, StyleSheet, ImageBackground, Image } from 'react-native';
import { StatusBar } from 'expo-status-bar';

// Import screens for each label
import BuildItRecordsHome from './src/screens/BuildItRecords/HomeScreen';
import BuildItTechHome from './src/screens/BuildItTech/HomeScreen';
import BuildItDeepHome from './src/screens/BuildItDeep/HomeScreen';
import AboutScreen from './src/screens/AboutScreen';
import ContactScreen from './src/screens/ContactScreen';

// Import images
const backgroundImage = require('./src/assets/png/records/BuildIt_Records_Square.png');
const recordsLogo = require('./src/assets/png/records/BuildItRecords.png');
const techLogo = require('./src/assets/png/tech/BuildIt_Tech.png');
const deepLogo = require('./src/assets/png/deep/BuildIt_Deep.png');

const Stack = createNativeStackNavigator();
const Tab = createBottomTabNavigator();

// Label selection component
const LabelSelector = ({ currentLabel, onSelectLabel }) => (
  <View style={styles.labelSelector}>
    <TouchableOpacity 
      style={[styles.labelButton, currentLabel === 'records' && styles.activeLabel]}
      onPress={() => onSelectLabel('records')}
    >
      <Image 
        source={recordsLogo}
        style={styles.labelLogo}
        resizeMode="contain"
      />
    </TouchableOpacity>
    <TouchableOpacity 
      style={[styles.labelButton, currentLabel === 'tech' && styles.activeLabel]}
      onPress={() => onSelectLabel('tech')}
    >
      <Image 
        source={techLogo}
        style={styles.labelLogo}
        resizeMode="contain"
      />
    </TouchableOpacity>
    <TouchableOpacity 
      style={[styles.labelButton, currentLabel === 'deep' && styles.activeLabel]}
      onPress={() => onSelectLabel('deep')}
    >
      <Image 
        source={deepLogo}
        style={styles.labelLogo}
        resizeMode="contain"
      />
    </TouchableOpacity>
  </View>
);

// Stack navigators for each label
const BuildItRecordsStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="BuildItRecordsHome" 
      component={BuildItRecordsHome}
      options={{ title: 'Build It Records' }}
    />
    <Stack.Screen name="About" component={AboutScreen} />
    <Stack.Screen name="Contact" component={ContactScreen} />
  </Stack.Navigator>
);

const BuildItTechStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="BuildItTechHome" 
      component={BuildItTechHome}
      options={{ title: 'Build It Tech' }}
    />
    <Stack.Screen name="About" component={AboutScreen} />
    <Stack.Screen name="Contact" component={ContactScreen} />
  </Stack.Navigator>
);

const BuildItDeepStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="BuildItDeepHome" 
      component={BuildItDeepHome}
      options={{ title: 'Build It Deep' }}
    />
    <Stack.Screen name="About" component={AboutScreen} />
    <Stack.Screen name="Contact" component={ContactScreen} />
  </Stack.Navigator>
);

export default function App() {
  const [currentLabel, setCurrentLabel] = useState('records');

  return (
    <NavigationContainer>
      <ImageBackground 
        source={backgroundImage}
        style={styles.backgroundImage}
        imageStyle={styles.backgroundImageStyle}
      >
        <View style={styles.container}>
          <LabelSelector currentLabel={currentLabel} onSelectLabel={setCurrentLabel} />
          {currentLabel === 'records' && <BuildItRecordsStack />}
          {currentLabel === 'tech' && <BuildItTechStack />}
          {currentLabel === 'deep' && <BuildItDeepStack />}
        </View>
      </ImageBackground>
      <StatusBar style="auto" />
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
    width: '100%',
    height: '100%',
  },
  backgroundImageStyle: {
    opacity: 0.2,
    resizeMode: 'repeat',
  },
  container: {
    flex: 1,
  },
  labelSelector: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    paddingVertical: 15,
    backgroundColor: 'rgba(26, 26, 26, 0.95)',
    borderBottomWidth: 1,
    borderBottomColor: '#333',
  },
  labelButton: {
    padding: 10,
    borderRadius: 5,
    width: '30%',
    maxWidth: 200,
    alignItems: 'center',
  },
  activeLabel: {
    backgroundColor: 'rgba(51, 51, 51, 0.8)',
  },
  labelLogo: {
    width: '100%',
    height: 40,
  },
});
