import React from 'react';
import { View, Text, Image, StyleSheet, TouchableOpacity, Linking } from 'react-native';

interface ReleaseCardProps {
  title: string;
  artist: string;
  artwork: string;
  releaseDate: string;
  genre: string;
  beatportLink?: string;
  label: 'records' | 'tech' | 'deep';
}

const ReleaseCard: React.FC<ReleaseCardProps> = ({
  title,
  artist,
  artwork,
  releaseDate,
  genre,
  beatportLink,
  label,
}) => {
  const getLabelColor = () => {
    switch (label) {
      case 'tech':
        return '#00ff00';
      case 'deep':
        return '#4a90e2';
      default:
        return '#ffffff';
    }
  };

  const handlePress = () => {
    if (beatportLink) {
      Linking.openURL(beatportLink);
    }
  };

  return (
    <TouchableOpacity style={styles.card} onPress={handlePress}>
      <Image source={{ uri: artwork }} style={styles.artwork} />
      <View style={styles.infoContainer}>
        <Text style={[styles.artist, { color: getLabelColor() }]}>{artist}</Text>
        <Text style={styles.title}>{title}</Text>
        <View style={styles.detailsRow}>
          <Text style={styles.genre}>{genre}</Text>
          <Text style={styles.date}>{releaseDate}</Text>
        </View>
      </View>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  card: {
    width: '100%',
    maxWidth: 300,
    backgroundColor: '#1a1a1a',
    borderRadius: 8,
    overflow: 'hidden',
    marginBottom: 20,
    marginHorizontal: 10,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  artwork: {
    width: '100%',
    aspectRatio: 1,
    backgroundColor: '#2a2a2a',
  },
  infoContainer: {
    padding: 12,
  },
  artist: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 4,
    color: '#ffffff',
  },
  title: {
    fontSize: 14,
    color: '#888888',
    marginBottom: 8,
  },
  detailsRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  genre: {
    fontSize: 12,
    color: '#666666',
  },
  date: {
    fontSize: 12,
    color: '#666666',
  },
});

export default ReleaseCard;
