import React from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import ReleasesGrid from '../../components/ReleasesGrid';

// Mock releases for Build It Deep
const mockReleases = [
  {
    id: 'deep1',
    title: 'Midnight Waters EP',
    artist: 'Deep Diver',
    artwork: 'https://via.placeholder.com/300',
    releaseDate: '2024-02-05',
    genre: 'Deep House',
    beatportLink: 'https://www.beatport.com/release/deep-sample/1',
    spotifyLink: 'https://open.spotify.com/album/deep1',
    soundcloudLink: 'https://soundcloud.com/deepdiver/midnight-waters',
    tracks: [
      {
        id: 'deep_t1',
        title: 'Midnight Waters (Original Mix)',
        artist: 'Deep Diver',
        duration: '6:30',
        spotifyId: 'spotify:track:deep1_1',
      },
      {
        id: 'deep_t2',
        title: 'Ocean Floor (Original Mix)',
        artist: 'Deep Diver',
        duration: '7:15',
        spotifyId: 'spotify:track:deep1_2',
      },
    ],
    label: 'deep',
  },
  {
    id: 'deep2',
    title: 'Soulful Journey',
    artist: 'Melodic Mind',
    artwork: 'https://via.placeholder.com/300',
    releaseDate: '2024-01-25',
    genre: 'Melodic House',
    beatportLink: 'https://www.beatport.com/release/deep-sample/2',
    spotifyLink: 'https://open.spotify.com/album/deep2',
    tracks: [
      {
        id: 'deep_t3',
        title: 'Soulful Journey (Original Mix)',
        artist: 'Melodic Mind',
        duration: '7:45',
        spotifyId: 'spotify:track:deep2_1',
      },
      {
        id: 'deep_t4',
        title: 'Inner Peace (Original Mix)',
        artist: 'Melodic Mind',
        duration: '8:00',
        spotifyId: 'spotify:track:deep2_2',
      },
    ],
    label: 'deep',
  },
  {
    id: 'deep3',
    title: 'Ethereal Depths',
    artist: 'Harmony Wave',
    artwork: 'https://via.placeholder.com/300',
    releaseDate: '2024-01-15',
    genre: 'Progressive House',
    beatportLink: 'https://www.beatport.com/release/deep-sample/3',
    spotifyLink: 'https://open.spotify.com/album/deep3',
    tracks: [
      {
        id: 'deep_t5',
        title: 'Ethereal Depths (Original Mix)',
        artist: 'Harmony Wave',
        duration: '8:30',
        spotifyId: 'spotify:track:deep3_1',
      },
      {
        id: 'deep_t6',
        title: 'Celestial Dance (Original Mix)',
        artist: 'Harmony Wave',
        duration: '7:30',
        spotifyId: 'spotify:track:deep3_2',
      },
    ],
    label: 'deep',
  },
];

const BuildItDeepHome = ({ navigation }) => {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>BUILD IT DEEP</Text>
        <Text style={styles.subtitle}>Deep & Melodic House Label</Text>
      </View>

      <View style={styles.content}>
        <Text style={styles.sectionTitle}>Latest Releases</Text>
        <ReleasesGrid releases={mockReleases} label="deep" />

        <TouchableOpacity 
          style={styles.button}
          onPress={() => navigation.navigate('About')}
        >
          <Text style={styles.buttonText}>About the Label</Text>
        </TouchableOpacity>

        <TouchableOpacity 
          style={styles.button}
          onPress={() => navigation.navigate('Contact')}
        >
          <Text style={styles.buttonText}>Contact Us</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#121212',
  },
  header: {
    padding: 20,
    alignItems: 'center',
    borderBottomWidth: 1,
    borderBottomColor: '#333',
    backgroundColor: 'rgba(26, 26, 26, 0.9)',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#4a90e2', // Blue color for Deep label
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 16,
    color: '#888888',
  },
  content: {
    padding: 20,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#1a3f5c', // Darker blue for Deep buttons
    padding: 15,
    borderRadius: 8,
    alignItems: 'center',
    marginTop: 20,
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default BuildItDeepHome;
