import React from 'react';
import { View, StyleSheet, ScrollView } from 'react-native';
import ReleaseCard from './ReleaseCard';

interface Release {
  id: string;
  title: string;
  artist: string;
  artwork: string;
  releaseDate: string;
  genre: string;
  beatportLink?: string;
  label: 'records' | 'tech' | 'deep';
}

interface ReleasesGridProps {
  releases: Release[];
  label: 'records' | 'tech' | 'deep';
}

const ReleasesGrid: React.FC<ReleasesGridProps> = ({ releases, label }) => {
  const filteredReleases = releases.filter(release => release.label === label);

  return (
    <View style={styles.container}>
      <View style={styles.grid}>
        {filteredReleases.map((release) => (
          <View key={release.id} style={styles.cardContainer}>
            <ReleaseCard
              title={release.title}
              artist={release.artist}
              artwork={release.artwork}
              releaseDate={release.releaseDate}
              genre={release.genre}
              beatportLink={release.beatportLink}
              label={label}
            />
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#121212',
  },
  grid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    padding: 10,
  },
  cardContainer: {
    width: '100%',
    maxWidth: 300,
    marginBottom: 20,
  },
});

export default ReleasesGrid;
