import React, { useState } from 'react';
import { View, Text, Image, StyleSheet, TouchableOpacity, Modal, Linking } from 'react-native';
import SpotifyService from '../services/SpotifyService';

interface Track {
  id: string;
  title: string;
  artist: string;
  duration: string;
  spotifyId?: string;
  previewUrl?: string;
}

interface ReleaseCardProps {
  title: string;
  artist: string;
  artwork: string;
  releaseDate: string;
  genre: string;
  beatportLink?: string;
  spotifyLink?: string;
  tracks: Track[];
  label: 'records' | 'tech' | 'deep';
}

const ReleaseCard: React.FC<ReleaseCardProps> = ({
  title,
  artist,
  artwork,
  releaseDate,
  genre,
  beatportLink,
  spotifyLink,
  tracks,
  label,
}) => {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedTrack, setSelectedTrack] = useState<Track | null>(null);
  const spotifyService = new SpotifyService(
    process.env.REACT_APP_SPOTIFY_CLIENT_ID!,
    window.location.origin + '/callback'
  );

  const getLabelColor = () => {
    switch (label) {
      case 'tech':
        return '#00ff00';
      case 'deep':
        return '#4a90e2';
      default:
        return '#ffffff';
    }
  };

  const handlePress = () => {
    setModalVisible(true);
  };

  const handleBeatportClick = () => {
    if (beatportLink) {
      Linking.openURL(beatportLink);
    }
  };

  const handleSpotifyClick = () => {
    if (spotifyLink) {
      Linking.openURL(spotifyLink);
    }
  };

  const handleAddToLibrary = async (track: Track) => {
    if (!track.spotifyId) return;

    try {
      await spotifyService.initialize();
      const success = await spotifyService.addToLibrary(track.spotifyId);
      if (success) {
        // Show success message
        alert('Added to your Spotify library!');
      }
    } catch (error) {
      if (error.message === 'Not authenticated with Spotify') {
        spotifyService.login();
      }
    }
  };

  const handleAddToPlaylist = async (track: Track) => {
    if (!track.spotifyId) return;

    try {
      await spotifyService.initialize();
      const playlists = await spotifyService.getUserPlaylists();
      setSelectedTrack(track);
      // Show playlist selection modal
      // Implementation depends on your UI framework
    } catch (error) {
      if (error.message === 'Not authenticated with Spotify') {
        spotifyService.login();
      }
    }
  };

  return (
    <>
      <TouchableOpacity style={styles.card} onPress={handlePress}>
        <Image source={{ uri: artwork }} style={styles.artwork} />
        <View style={styles.infoContainer}>
          <Text style={[styles.artist, { color: getLabelColor() }]}>{artist}</Text>
          <Text style={styles.title}>{title}</Text>
          <View style={styles.detailsRow}>
            <Text style={styles.genre}>{genre}</Text>
            <Text style={styles.date}>{releaseDate}</Text>
          </View>
        </View>
      </TouchableOpacity>

      <Modal
        visible={modalVisible}
        animationType="slide"
        transparent={true}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Image source={{ uri: artwork }} style={styles.modalArtwork} />
            <Text style={styles.modalTitle}>{title}</Text>
            <Text style={styles.modalArtist}>{artist}</Text>

            <View style={styles.buttonContainer}>
              {beatportLink && (
                <TouchableOpacity style={styles.storeButton} onPress={handleBeatportClick}>
                  <Text style={styles.buttonText}>Buy on Beatport</Text>
                </TouchableOpacity>
              )}
              {spotifyLink && (
                <TouchableOpacity style={styles.storeButton} onPress={handleSpotifyClick}>
                  <Text style={styles.buttonText}>Open in Spotify</Text>
                </TouchableOpacity>
              )}
            </View>

            <View style={styles.trackList}>
              {tracks.map((track) => (
                <View key={track.id} style={styles.trackItem}>
                  <Text style={styles.trackTitle}>{track.title}</Text>
                  <View style={styles.trackActions}>
                    {track.spotifyId && (
                      <>
                        <TouchableOpacity
                          style={styles.actionButton}
                          onPress={() => handleAddToLibrary(track)}
                        >
                          <Text style={styles.actionButtonText}>â™¥</Text>
                        </TouchableOpacity>
                        <TouchableOpacity
                          style={styles.actionButton}
                          onPress={() => handleAddToPlaylist(track)}
                        >
                          <Text style={styles.actionButtonText}>+</Text>
                        </TouchableOpacity>
                      </>
                    )}
                  </View>
                </View>
              ))}
            </View>

            <TouchableOpacity
              style={styles.closeButton}
              onPress={() => setModalVisible(false)}
            >
              <Text style={styles.closeButtonText}>Close</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </>
  );
};

const styles = StyleSheet.create({
  card: {
    width: '100%',
    maxWidth: 300,
    backgroundColor: '#1a1a1a',
    borderRadius: 8,
    overflow: 'hidden',
    marginBottom: 20,
    marginHorizontal: 10,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  artwork: {
    width: '100%',
    aspectRatio: 1,
    backgroundColor: '#2a2a2a',
  },
  infoContainer: {
    padding: 12,
  },
  artist: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 4,
  },
  title: {
    fontSize: 14,
    color: '#888888',
    marginBottom: 8,
  },
  detailsRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  genre: {
    fontSize: 12,
    color: '#666666',
  },
  date: {
    fontSize: 12,
    color: '#666666',
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.9)',
  },
  modalContent: {
    width: '90%',
    maxWidth: 500,
    backgroundColor: '#1a1a1a',
    borderRadius: 12,
    padding: 20,
    alignItems: 'center',
  },
  modalArtwork: {
    width: 200,
    height: 200,
    borderRadius: 8,
    marginBottom: 20,
  },
  modalTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: 8,
    textAlign: 'center',
  },
  modalArtist: {
    fontSize: 16,
    color: '#888888',
    marginBottom: 20,
    textAlign: 'center',
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    marginBottom: 20,
  },
  storeButton: {
    backgroundColor: '#333',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 20,
    marginHorizontal: 10,
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 14,
    fontWeight: 'bold',
  },
  trackList: {
    width: '100%',
    marginTop: 20,
  },
  trackItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#333',
  },
  trackTitle: {
    color: '#ffffff',
    fontSize: 14,
    flex: 1,
  },
  trackActions: {
    flexDirection: 'row',
  },
  actionButton: {
    padding: 8,
    marginLeft: 10,
  },
  actionButtonText: {
    color: '#ffffff',
    fontSize: 18,
  },
  closeButton: {
    marginTop: 20,
    padding: 10,
  },
  closeButtonText: {
    color: '#888888',
    fontSize: 16,
  },
});

export default ReleaseCard;
