import React from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import ReleasesGrid from '../../components/ReleasesGrid';

// This would typically come from your Symphonic Distribution API
const mockReleases = [
  {
    id: '1',
    title: 'Sample Release 1',
    artist: 'Artist Name',
    artwork: 'https://via.placeholder.com/300',
    releaseDate: '2024-01-01',
    genre: 'House',
    beatportLink: 'https://www.beatport.com/release/sample/1',
    label: 'records',
  },
  // Add more mock releases here
];

const BuildItRecordsHome = ({ navigation }) => {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>BUILD IT RECORDS</Text>
        <Text style={styles.subtitle}>Underground Electronic Music Label</Text>
      </View>

      <View style={styles.content}>
        <Text style={styles.sectionTitle}>Latest Releases</Text>
        <ReleasesGrid releases={mockReleases} label="records" />

        <TouchableOpacity 
          style={styles.button}
          onPress={() => navigation.navigate('About')}
        >
          <Text style={styles.buttonText}>About the Label</Text>
        </TouchableOpacity>

        <TouchableOpacity 
          style={styles.button}
          onPress={() => navigation.navigate('Contact')}
        >
          <Text style={styles.buttonText}>Contact Us</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#121212',
  },
  header: {
    padding: 20,
    alignItems: 'center',
    borderBottomWidth: 1,
    borderBottomColor: '#333',
    backgroundColor: 'rgba(26, 26, 26, 0.9)',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 16,
    color: '#888888',
  },
  content: {
    padding: 20,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#333',
    padding: 15,
    borderRadius: 8,
    alignItems: 'center',
    marginTop: 20,
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default BuildItRecordsHome;
