import React from 'react';
import { View, StyleSheet, ScrollView } from 'react-native';
import ReleaseCard from './ReleaseCard';

interface Track {
  id: string;
  title: string;
  artist: string;
  duration: string;
  spotifyId?: string;
  previewUrl?: string;
}

interface Release {
  id: string;
  title: string;
  artist: string;
  artwork: string;
  releaseDate: string;
  genre: string;
  beatportLink?: string;
  spotifyLink?: string;
  tracks: Track[];
  label: 'records' | 'tech' | 'deep';
}

interface ReleasesGridProps {
  releases: Release[];
  label: 'records' | 'tech' | 'deep';
}

const ReleasesGrid: React.FC<ReleasesGridProps> = ({ releases, label }) => {
  const filteredReleases = releases.filter(release => release.label === label);

  // Sort releases by date (newest first)
  const sortedReleases = [...filteredReleases].sort((a, b) => 
    new Date(b.releaseDate).getTime() - new Date(a.releaseDate).getTime()
  );

  // Get latest release and prior releases
  const latestRelease = sortedReleases[0];
  const priorReleases = sortedReleases.slice(1, 21); // Get up to 20 prior releases

  return (
    <ScrollView style={styles.container}>
      {latestRelease && (
        <View style={styles.latestReleaseContainer}>
          <View style={styles.latestReleaseWrapper}>
            <ReleaseCard
              title={latestRelease.title}
              artist={latestRelease.artist}
              artwork={latestRelease.artwork}
              releaseDate={latestRelease.releaseDate}
              genre={latestRelease.genre}
              beatportLink={latestRelease.beatportLink}
              spotifyLink={latestRelease.spotifyLink}
              tracks={latestRelease.tracks}
              label={label}
              isLatestRelease={true}
            />
          </View>
        </View>
      )}

      <View style={styles.priorReleasesContainer}>
        {priorReleases.map((release) => (
          <ReleaseCard
            key={release.id}
            title={release.title}
            artist={release.artist}
            artwork={release.artwork}
            releaseDate={release.releaseDate}
            genre={release.genre}
            beatportLink={release.beatportLink}
            spotifyLink={release.spotifyLink}
            tracks={release.tracks}
            label={label}
            isLatestRelease={false}
          />
        ))}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#121212',
  },
  latestReleaseContainer: {
    paddingVertical: 20,
    marginBottom: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#333',
    alignItems: 'center',
  },
  latestReleaseWrapper: {
    width: '80%',
    maxWidth: 400,
  },
  priorReleasesContainer: {
    paddingHorizontal: 20,
  },
});

export default ReleasesGrid;
